'use client'

import { useEffect, useState } from 'react'
import DashboardHero from './DashboardHero'
import DashboardLinks from './DashboardLinks'
import LogoutButton from './LogoutButton'
import { LEARNER_DASHBOARD_HERO, LEARNER_DASHBOARD_LINKS } from './Dashboard'

// Mock user data for SSG - will be replaced with actual auth context on client
const MOCK_USER = {
  id: '1',
  firstName: 'John',
  role: 'Learner' as const
}

export default function ProtectedDashboard() {
  const [user, setUser] = useState(MOCK_USER)
  const [isLoading, setIsLoading] = useState(false)

// In a real app, this would fetch from auth context
  useEffect(() => {
    // Simulate checking auth status
    const authToken = localStorage.getItem('authToken')
    if (!authToken) {
      if (typeof window !== 'undefined') {
        window.location.href = './login-page.html'
      }
      return
    }
    
    // Get user from localStorage or auth context
    const storedUser = localStorage.getItem('user')
    if (storedUser) {
      try {
        setUser(JSON.parse(storedUser))
      } catch (e) {
        console.error('Failed to parse user data')
      }
    }
    setIsLoading(false)
  }, [])

const handleLogout = async () => {
    setIsLoading(true)
    try {
      // Clear auth data
      localStorage.removeItem('authToken')
      localStorage.removeItem('user')
      
      // Redirect to login
      if (typeof window !== 'undefined') {
        window.location.href = './login-page.html'
      }
    } catch (error) {
      console.error('Logout failed:', error)
      setIsLoading(false)
    }
  }

  return (
    <div className="space-y-8">
      {/* Welcome Section */}
      <div className="space-y-2">
        <h1 className="text-4xl font-bold text-foreground">
          Welcome back, {user.firstName}! ðŸ‘‹
        </h1>
        <p className="text-lg text-muted-foreground">
          Continue your learning journey and explore new courses
        </p>
      </div>

      {/* Hero Section */}
      <DashboardHero hero={LEARNER_DASHBOARD_HERO} />

      {/* Dashboard Links */}
      <div className="space-y-4">
        <h2 className="text-2xl font-semibold text-foreground">Quick Access</h2>
        <DashboardLinks links={LEARNER_DASHBOARD_LINKS} />
      </div>

      {/* Logout Button */}
      <div className="flex justify-end pt-4">
        <LogoutButton 
          onLogout={handleLogout} 
          isLoading={isLoading}
        />
      </div>
    </div>
  )
}
