import React from "react";
import { useParams } from "react-router-dom";

const CourseViewer: React.FC = () => {
  const { id } = useParams();

  return (
    <div className="page">
      <h2>Course Viewer</h2>
      <h3>Course ID: {id}</h3>

      <p>This is where videos, PDFs, and lessons will display.</p>

      <div className="card">
        <h4>Lesson 1</h4>
        <p>Welcome to this course!</p>
      </div>

      <div className="card">
        <h4>Lesson 2</h4>
        <p>Understanding the basics...</p>
      </div>
    </div>
  );
};

export default CourseViewer;
